import { IcSpinner } from "icons/IcSpinner";
import { ReactNode } from "react";

type Props = {
  loading?: boolean;
  children?: ReactNode;
}

const ButtonField = ({
  loading,
  children,
}: Props) => {
  return (
    <div className="btn-main">
      {!loading
        ? children
        : <div style={{ display: 'flex'}}>
          <IcSpinner width="32px" height="32px" />
          {children}
        </div>
      }
    </div>
  )
};

export default ButtonField;
