import ButtonField from "components/ButtonField";
import { ReactElement } from "react";

type Props = {
  width?: string;
  children?: ReactElement;
  title?: string;
  isShow?: boolean;
}

const ModalLayout = ({
  title,
  width = '70%',
  children,
}: Props) => {
  return (
    <div className="wrapper-modal">
      <div className="modal-body" style={{ width }}>
        {!children ? (
          <div style={{ display: 'flex', flexDirection: 'column', rowGap: '1rem' }}>
            <div style={{ fontSize: '22px', textAlign: 'center' }}>{title}</div>
            <div style={{ display: 'flex', columnGap: '1rem', justifyContent: 'center' }}>
              <ButtonField>Confirm</ButtonField>
              <ButtonField color='primary'>Cancel</ButtonField>
            </div>
          </div>
        ) : children}
      </div>
    </div>
  );
};

export default ModalLayout;
