import { Pizza } from "models/pizza.model";
import { useState } from "react";
import CardPizza from "sections/CardPizza";

const HomePage = () => {

  // Props cho phép chúng ta giao tiếp giữa các components với nhau bằng cách truyền tham số qua lại giữa các components.

  // Chúng ta có thể truyền dữ liệu (data) từ component Cha xuống component Con.

  // Khi truyền props (dữ liệu data) từ component Cha sang component Con thì component Con có thể sử dụng được props đó.

  // Khi truyền props từ Cha sang Con thì props không thể bị thay đổi trực tiếp bên trong component Con.


  const [pizzas, setPizzas] = useState<Pizza[]>([
    { id: 1, title: 'Pizza Thịt Bầm', description: 'Thịt bầm, sốt cà chua' },
    { id: 2, title: 'Pizza Phô Mai', description: 'Phô mai, muối' },
    { id: 3, title: 'Pizza Hải Sản', description: 'Vị tôm, cua' },
  ]);

  const handleRemovePizza = (id: number) => {
    const indexPizza = pizzas.findIndex(item => item.id === id);
    let newPizzas = [...pizzas];
    newPizzas.splice(indexPizza, 1);
    setPizzas(newPizzas);
  };

  return (
    <div style={{ height: 'calc(100vh - 309px)', padding: '4rem 4rem', overflowY: 'auto' }}>
      <div className="wrapper-card-items">
        {
          pizzas.map(item =>
            <CardPizza key={item.id} id={item.id} title={item.title} description={item.description} />
          )
        }
      </div>
    </div>
  )
};

export default HomePage;
