import ButtonField from "components/ButtonField";
import TextField from "components/TextField";
import LoadingLayout from "layouts/LoadingLayout";
import { Pizza } from "models/pizza.model";
import { useState } from "react";

const CreatePizzaPage = () => {
  const [pizza, setPizza] = useState<Pizza>({});

  return (
    <LoadingLayout loading={false}>
      <div className="wrapper-create">
        <div style={{ width: '50%' }}>
          <h1>Create New Pizza</h1>
          <div className="form-control">
            <div style={{ marginBottom: '1rem' }}>Product Name:</div>
            <TextField
              placeholder="Enter Product Name Here!"
              onChange={(e) => setPizza({ ...pizza, productName: e })}
            />
          </div>
          <div className="form-control">
            <div style={{ marginBottom: '1rem' }}>Description</div>
            <TextField
              placeholder="Enter Description Here!"
              onChange={(e) => setPizza({ ...pizza, description: e })}
            />
          </div>
          <div className="form-control">
            <div style={{ marginBottom: '1rem' }}>Price:</div>
            <TextField
              placeholder="Enter Price Here!"
              onChange={(e) => setPizza({ ...pizza, price: e })}
            />
          </div>
          <div className="form-control" style={{ display: 'flex' }}>
            <ButtonField onClick={() => console.log(pizza)}>Create Pizza</ButtonField>
          </div>
        </div>
      </div>
    </LoadingLayout>
  )
};

export default CreatePizzaPage;
