import { useEffect } from "react";

type Props = {
  count: number;
  setCount: (count: number) => void;
}

const CountPizza = ({ count, setCount }: Props) => {
  useEffect(() => {
    return () => {
      setCount(0);
    }
  }, []);

  return (
    <>
      <div>{count}</div>
      <button onClick={() => setCount(count + 1)}>Increase</button>
    </>
  );
};

export default CountPizza;
