import ButtonField from "components/ButtonField";
import TextField from "components/TextField";
import { Pizza } from "models/pizza.model";
import { useEffect, useMemo, useState } from "react";
import CardPizza from "sections/CardPizza";

const HomePage = () => {
  const [pizzas, setPizzas] = useState<Pizza[]>([]);

  const [count, setCount] = useState(0);

  useEffect(() => {
    fetch('http://localhost:3000/products')
      .then(res => res.json())
      .then(({ data }) => setPizzas(data));
  }, [])

  // useMemo
  // Trả lại 1 value
  // Cache lại data
  // Sẽ chạy lại nếu tham số trong mảng dependency thay đổi

  const tinhTong = useMemo(() => {
    return count + 1;
  }, []);

  return (
    <>
      <div style={{ height: 'calc(100vh - 309px)', padding: '4rem 4rem', overflowY: 'auto' }}>
        <TextField placeholder="Enter Search!" width="250px" />
        <div className="wrapper-card-items">
          {
            pizzas.map((item, index) =>
              <CardPizza
                key={index}
                id={item.id}
                productName={item.productName}
                description={item.description}
              />
            )
          }
          <div style={{ display: 'flex', justifyContent: 'center', width: '100%' }}>
            <ButtonField>Show more</ButtonField>
          </div>

          {/* Bai 20 */}
          {tinhTong}

          <button onClick={() => setCount(count + 1)}>Increase</button>
          {count}
        </div>
      </div>
    </>
  )
};

export default HomePage;
